import React from "react";
import { WithMarginProp } from "../styled/withMargin";
import { SVG } from "./SVG";

const MONO_FILL_DARK = "#2C3A4B";
const MONO_FILL_LIGHT = "#ffffff";

function getFillColors(isOnDarkBackground?: boolean, isMonoColor?: boolean) {
  let typographyFill = undefined;
  let logoFill = undefined;
  if (isOnDarkBackground) {
    typographyFill = MONO_FILL_LIGHT;
  }
  if (isMonoColor) {
    typographyFill = isOnDarkBackground ? MONO_FILL_LIGHT : MONO_FILL_DARK;
    logoFill = isOnDarkBackground ? MONO_FILL_LIGHT : MONO_FILL_DARK;
  }
  return {
    typographyFill,
    logoFill,
  };
}

interface BaseLogoProps extends WithMarginProp {
  isOnDarkBackground?: boolean;
  isMonoColor?: boolean;
  width?: number;
  height?: number;
}

interface LogoProps extends BaseLogoProps {
  variant?: "horizontal" | "vertical" | "ouroboros";
  shouldIncludeLloyds?: boolean;
}

export function Logo({
  variant = "horizontal",
  shouldIncludeLloyds = true,
  isOnDarkBackground = false,
  isMonoColor = false,
  margin,
  width,
  height,
}: LogoProps) {
  const baseProps = {
    isOnDarkBackground,
    isMonoColor,
    margin,
  };

  if (variant === "ouroboros") {
    return (
      <Ouroboros {...baseProps} width={width || 40} height={height || 40} />
    );
  }

  if (variant === "vertical") {
    return shouldIncludeLloyds ? (
      <VerticalWithLloyds
        {...baseProps}
        width={width || 68}
        height={height || 80}
      />
    ) : (
      <VerticalWithoutLloyds
        {...baseProps}
        width={width || 68}
        height={height || 68}
      />
    );
  }

  return shouldIncludeLloyds ? (
    <HorizontalWithLloyds
      {...baseProps}
      width={width || 146}
      height={height || 40}
    />
  ) : (
    <HorizontalWithoutLloyds
      {...baseProps}
      width={width || 146}
      height={height || 40}
    />
  );
}

